(function() {
  'use strict';

  function swap(obj, prop) {
    var prop2 = '_' + prop;
    [obj[prop], obj[prop2]] = [obj[prop2], obj[prop]];
  }

  function createCrack(x, y) {
    var crack = document.createElement('div');
    crack.className = 'cracked-screen-image';
    crack.style.position = 'fixed';
    crack.style.top = y - (50) + 'px';
    crack.style.left = x - (50) + 'px';
    crack.style.width = '100px';
    crack.style.height = '100px';
    crack.style.zIndex = '10000000';
    crack.style.pointerEvents = 'none';
    crack.style.display = 'block';
    crack.style.transition = 'opacity 1s, transform 1s, height 1s, width 1s';
    crack.style.borderRadius = '100%';
    crack.style.backgroundSize = '320px auto';
    crack.style.backgroundPosition = 'calc(50% - 50px) calc(50% + 10px)';
    crack.style.backgroundRepeat = 'no-repeat';
    crack.style.opacity = '0.85';
    //crack.style.backgroundColor = 'rgba(0, 255, 0, .3)';
    crack.style.transform = 'none';
    crack.style._transform = 'translateX(-250px) translateY(-250px)';
    crack.style._height = '600px';
    crack.style._width = '600px';
    crack.style._opacity = '1';

    // Cracked glass image modified from
    // http://commons.wikimedia.org/wiki/File:Broken_glass_1040822_Nevit.jpg
    // Copyright Nevit Dilmen
  crack.style.backgroundImage = 'url(data:image/png;base64,)';

    document.body.appendChild(crack);

    var animateCrack = function() {
      var s = crack.style;
      swap(s, 'transform');
      swap(s, 'height');
      swap(s, 'width');
      swap(s, 'opacity');
    }
    setTimeout(animateCrack, 0);
    setTimeout(function() {
      crack.remove();
    }, 1100);
  }

  function initCrackedScreen() {
    var touchHandler = function(evt) {
      var touch = evt.touches[0];
      createCrack(touch.screenX, touch.screenY);
    };
    document.body.addEventListener('touchstart', touchHandler);

    navigator.mozApps.mgmt.addEventListener('enabledstatechange', function kill(event) {
      var app = event.application;
      if (app.manifest.name === 'Cracked Screen' && !app.enabled) {
        navigator.mozApps.mgmt.removeEventListener('enabledstatechange', kill);
        document.body.removeEventListener('touchstart', touchHandler);
      }
    });
  }

  if (document.documentElement) {
    initCrackedScreen();
  } else {
    window.addEventListener('DOMContentLoaded', initCrackedScreen);
  }

}());
